function y_t1 = RK4step(f, param, t0, y_t0, dt)    c = [0, 1/2, 1/2, 1]; % In this case a = diag(c);    b = [1/6, 1/3, 1/3, 1/6];        k1 = f(t0, y_t0, param);        k2 = f(t0 + dt.*c(2), y_t0 + dt.*k1.*c(2), param);    k3 = f(t0 + dt.*c(3), y_t0 + dt.*k2.*c(3), param);     k4 = f(t0 + dt.*c(4), y_t0 + dt.*k3.*c(4), param);        y_t1 = y_t0 + dt .* ( b(1).*k1 + b(2).*k2 + b(3).*k3 + b(4).*k4);end