function y_t1 = RK4step(f, param, t0, y_t0, dt)    c = [0, 1/2, 1/2, 1]; % In this case a = diag(c);    b = [1/6; 1/3; 1/3; 1/6];    k = zeros(length(y_t0), 4);        k(:,1) = f(t0, y_t0, param);    k(:,2) = f(t0 + dt*c(2), y_t0 + dt*k(:,1)*c(2), param);    k(:,3) = f(t0 + dt*c(3), y_t0 + dt*k(:,2)*c(3), param);    k(:,4) = f(t0 + dt*c(4), y_t0 + dt*k(:,3)*c(4), param);        y_t1 = y_t0 + dt .* k * b;end